import React, { useState, useEffect } from "react";
import FilterForm from "./component/FilterForm";
import { Grid, Button, Typography } from "@mui/material";
import { styled } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Paper from "@mui/material/Paper";
import "./App.css";
import Tabtable from "./component/Tabtable";
import CircularProgress from "@mui/material/CircularProgress"; // 用於加載指示
import Alert from "@mui/material/Alert"; // 用於錯誤提示

const App = () => {
  const [images, setImages] = useState({});
  const [tableData, setTableData] = useState([]);
  const [error, setError] = useState("");
  const [searched, setSearched] = useState(false);
  const [loading, setLoading] = useState(false);
  useEffect(() => {
    console.log('tableData 已更新:', tableData);
  }, [tableData]);
  const handleSearch = async (filters) => {
    try {
      setError("");
      setSearched(true);
      setLoading(true);
      const response = await fetch("http://localhost:5000/search", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(filters),
      });
      if (!response.ok) {
        throw new Error("無效輸入/連線失敗");
      }

      const data = await response.json();
      console.log(data["tabledatas"])
      if (data["success"]) {
        const filteredImages = {};
        for (const [key, value] of Object.entries(data["images"])) {
          if (value) {
            filteredImages[key] = value;
          }
        }
        setImages(filteredImages);
        // 確保 tableData 是一個有效的 JSON 對象
        const filtertable = {}
        let parsedTableData = [];
        if (typeof data["tabledatas"] === 'string') {
          try {
            parsedTableData = JSON.parse(data["tabledatas"]);
            console.log('parsedTableData:', parsedTableData);
          } catch (parseError) {
            console.error("解析 tableData 時出錯:", parseError);
            setError("資料格式錯誤。");
          }
        } else if (Array.isArray(data["tabledatas"])) {
          parsedTableData = data["tabledatas"];
        } else {
          console.warn("未知的 tableData 格式:", data["tabledatas"]);
        }
        // for (const [key, value] of Object.entries(data["tabledatas"])) {
        //   filtertable[key]=JSON.parse()


        setTableData(parsedTableData);

      } else {
        setImages({});
        setTableData({});
        setError(data["message"] || "發生錯誤。");
      }
    } catch (error) {
      console.error("Error fetching data:", error);
      setImages({});
      setTableData({});
      setError(error.message || "發生錯誤。");
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = (base64Image, filename) => {
    const link = document.createElement("a");
    link.href = `data:image/png;base64,${base64Image}`;
    link.download = `${filename}.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const hasImages = Object.keys(images).length > 0;
  const hasTableData = tableData.length > 0;

  useEffect(() => {
    if (searched && !loading && !hasImages && !hasTableData && !error) {
      setError("沒有資料");
    }
  }, [searched, loading, hasImages, hasTableData, error]);

  return (
    <div>
      <header>
        <h1 className="title">8891報表</h1>
        <hr />
      </header>
      <main>
        <FilterForm onSearch={handleSearch} />
        {error && <Alert severity="error" className="error-message">{error}</Alert>} {/* 顯示錯誤消息 */}
        {loading && (
          <Box display="flex" justifyContent="center" my={2}>
            <CircularProgress />
          </Box>
        )} {/* 顯示加載消息 */}
        {!loading && searched && !hasImages && !hasTableData && !error && (
          <Typography variant="h6" className="no-data-message">沒有資料</Typography> // 顯示“沒有資料”
        )}
        {hasImages && (
          <Grid container spacing={2}>
            {Object.keys(images).map((key, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <h3>{key}</h3>
                <Box sx={{ maxWidth: "100%" }}>
                  <img
                    src={`data:image/png;base64,${images[key]}`}
                    alt={key}
                    className="outputImg"
                    style={{ maxWidth: "100%", height: "auto" }}
                  />
                </Box>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => handleDownload(images[key], key)}
                  style={{ marginTop: "10px" }}
                >
                  下載圖片
                </Button>
              </Grid>
            ))}
          </Grid>
        )}
    <div>

      {Object.keys(tableData).length > 0 && (
        <Box mt={4}>
          <Tabtable tableData={tableData} />
        </Box>
      )}
  </div>
      </main>
    </div>
  );
};

export default App;
