import React, { useState, useEffect } from "react";
import FilterForm from "./component/FilterForm";
import { Grid, Button, Typography } from "@mui/material";
import { styled } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Paper from "@mui/material/Paper";
import "./App.css";
import Tabtable from "./component/Tabtable";
import CircularProgress from "@mui/material/CircularProgress"; // 用於加載指示
import Alert from "@mui/material/Alert"; // 用於錯誤提示

const App = () => {
  const [images, setImages] = useState({});
  const [tableData, setTableData] = useState({}); // 新增狀態以存儲表格數據
  const [error, setError] = useState(""); // 用於存儲錯誤消息
  const [searched, setSearched] = useState(false); // 用於追踪用戶是否進行了搜索
  const [loading, setLoading] = useState(false); // 用於追踪搜索請求的狀態

  const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: "center",
    color: theme.palette.text.secondary,
  }));

  const handleSearch = async (filters) => {
    try {
      setError(""); // 在每次搜索前清空錯誤消息
      setSearched(true); // 用戶進行了搜索
      setLoading(true); // 搜索請求進行中
      const response = await fetch("http://localhost:5000/search", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(filters),
      });
      if (!response.ok) {
        throw new Error("無效輸入/連線失敗");
      }

      const data = await response.json();
      if (data["success"]) {
        const filteredImages = {};
        for (const [key, value] of Object.entries(data["images"])) {
          if (value) { // 檢查圖像數據是否存在
            filteredImages[key] = value;
          }
        }
        console.log(data["tabledatas"])
        setImages(filteredImages); // 設置過濾後的 images
        setTableData(data["tabledatas"] || []); // 設置表格數據
      } else {
        setImages({}); // 清空 images
        setTableData([]); // 清空表格數據
        setError(data["message"] || "發生錯誤。"); // 設置錯誤消息
      }
    } catch (error) {
      console.error("Error fetching data:", error);
      setImages({}); // 清空 images
      setTableData([]); // 清空表格數據
      setError(error.message || "發生錯誤。");
    } finally {
      setLoading(false); // 搜索請求結束
    }
  };

  const handleDownload = (base64Image, filename) => {
    const link = document.createElement("a");
    link.href = `data:image/png;base64,${base64Image}`;
    link.download = `${filename}.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const hasImages = Object.keys(images).length > 0;
  const hasTableData = tableData.length > 0;

  useEffect(() => {
    if (searched && !loading && !hasImages && !hasTableData && !error) {
      setError("沒有資料");
    }
  }, [searched, loading, hasImages, hasTableData, error]);

  return (
    <div>
      <header>
        <h1 className="title">8891報表</h1>
        <hr />
      </header>
      <main>
        <FilterForm onSearch={handleSearch} />
        {error && <Alert severity="error" className="error-message">{error}</Alert>} {/* 顯示錯誤消息 */}
        {loading && (
          <Box display="flex" justifyContent="center" my={2}>
            <CircularProgress />
          </Box>
        )} {/* 顯示加載消息 */}
        {!loading && searched && !hasImages && !hasTableData && !error && (
          <Typography variant="h6" className="no-data-message">沒有資料</Typography> // 顯示“沒有資料”
        )}
        {hasImages && (
          <Grid container spacing={2}>
            {Object.keys(images).map((key, index) => (
              <Grid item xs={12} sm={6} md={4} key={index}>
                <h3>{key}</h3>
                <Box sx={{ maxWidth: "100%" }}>
                  <img
                    src={`data:image/png;base64,${images[key]}`}
                    alt={key}
                    className="outputImg"
                    style={{ maxWidth: "100%", height: "auto" }}
                  />
                </Box>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => handleDownload(images[key], key)}
                  style={{ marginTop: "10px" }}
                >
                  下載圖片
                </Button>
              </Grid>
            ))}
          </Grid>
        )}
        {hasTableData && (
          <Box mt={4}>
            <Tabtable tableData={tableData} />
          </Box>
        )} {/* 顯示表格數據 */}
      </main>
    </div>
  );
};

export default App;
