            input={<OutlinedInput label="車型" />}
            renderValue={(selected) => selected.join(", ")}
            MenuProps={MenuProps}
          >
            {getModels().map((model) => (
              <MenuItem key={model} value={model}>
                <Checkbox checked={selectedModels.indexOf(model) > -1} />
                <ListItemText primary={model} />
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        </Grid>
        <Grid item xs={12} sm={4} md={3}>
        <FormControl sx={{ width: 250 }}>
          <InputLabel id="model-select-label" sx={{ fontSize: 15 }}>
            顏色
          </InputLabel>
          <Select
            labelId="model-select-label"
            id="model-select"
            multiple
            value={selectedColor}
            onChange={handleColorChange}
            input={<OutlinedInput label="顏色" />}
            renderValue={(selected) => selected.join(", ")}
            MenuProps={MenuProps}
          >
            {Colorlist.map((c) => (
              <MenuItem key={c} value={c}>
                <Checkbox checked={selectedColor.indexOf(c) > -1} />
                <ListItemText primary={c} />
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        </Grid>
      
        <Grid item xs={12} sm={4} md={3}>
        <div className="inputItem">
          <Box sx={{ width: "120px" }}>
            <TextField
              id="outlined-basic"
              label="最低里程"
              value={lowMilage}
              onChange={handleLowMilage}
            />
          </Box>
          <Box sx={{ width: "120px" }}>
            <TextField
              id="outlined-basic"
              label="最高里程"
              value={highMilage}
              onChange={handleHighMilage}
            />
          </Box>
        </div>
        </Grid>
        <Grid item xs={12} sm={4} md={3}>
        <Box sx={{ width: "250px" }}>
          <FormControlLabel
            control={
              <Checkbox checked={videoCheck} onChange={handleVideoCheck} />
            }
            label="影片看車"
          />
        </Box>
        </Grid>
        <Grid item xs={12} sm={4} md={3}>
        <Box sx={{ width: "250px" }}>
          <FormControlLabel
            control={
              <Checkbox checked={verifyCheck} onChange={handleVerifyCheck} />
            }
            label="第三方鑑定"
          />
        </Box>
        </Grid>
        <Grid item xs={12} sm={4} md={3}>
        <FormControl sx={{ width: 250 }}>
          <InputLabel id="model-select-label" sx={{ fontSize: 15 }}>
            配備
          </InputLabel>
          <Select
            labelId="model-select-label"
            id="model-select"
            multiple
            value={selectedEquip}
            onChange={handleEquipChange}
            input={<OutlinedInput label="配備" />}
            renderValue={(selected) => selected.join(", ")}
            MenuProps={MenuProps}
          >
            {Equiplist.map((c) => (
              <MenuItem key={c} value={c}>
                <Checkbox checked={selectedEquip.indexOf(c) > -1} />
                <ListItemText primary={c} />
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        </Grid>
        <Grid item xs={12} sm={4} md={3}>
        <FormControl sx={{ width: 250 }}>
          <InputLabel id="city-select-label">縣市</InputLabel>
          <Select
            labelId="city-select-label"
            id="city-select"
            multiple
            value={selectedCity}
            onChange={handleCityChange}
            input={<OutlinedInput label="縣市" />}
            renderValue={(selected) => selected.join(", ")}
            MenuProps={MenuProps}
          >
            {Object.keys(Locationlist).map((city) => (
              <MenuItem key={city} value={city}>
                <Checkbox checked={selectedCity.indexOf(city) > -1} />
                <ListItemText primary={city} />
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        </Grid>
        <Grid item xs={12} sm={4} md={3}>
        <FormControl sx={{ width: 250 }}>
          <InputLabel id="loc-select-label">區域</InputLabel>
          <Select
            labelId="loc-select-label"
            id="loc-select"
            multiple
            value={selectedLoc}
            onChange={handleLocChange}
            input={<OutlinedInput label="區域" />}
            renderValue={(selected) => selected.join(", ")}
            MenuProps={MenuProps}
          >
            {getLoc().map((loc) => (
              <MenuItem key={loc} value={loc}>
                <Checkbox checked={selectedLoc.indexOf(loc) > -1} />
                <ListItemText primary={loc} />
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        </Grid>
        <Grid item xs={12} sm={4} md={3}>
        <div className="inputItem">
          <Box sx={{ width: "120px" }}>
            <TextField
              id="outlined-basic"
              label="最低瀏覽"
              value={lowView}
              onChange={handleLowView}
            />
          </Box>
          <Box sx={{ width: "120px" }}>
            <TextField
              id="outlined-basic"
              label="最高瀏覽"
              value={highView}
              onChange={handleHighView}
            />
          </Box>
        </div>
        </Grid>
        <Grid item xs={12} sm={4} md={3}>
        <div className="inputItem">
          <Box sx={{ width: "120px" }}>
            <TextField
              id="outlined-basic"
              label="最低諮詢"
              value={lowAsknum}
              onChange={handleLowAsknum}
            />
          </Box>
          <Box sx={{ width: "120px" }}>
            <TextField
              id="outlined-basic"
              label="最高諮詢"
              value={highAsknum}
              onChange={handleHighAsknum}
            />
          </Box>
        </div>
        </Grid>
        <Grid item xs={12} sm={4} md={3}>
        <Box sx={{ width: "400px" }}>
          <TextField
            id="outlined-basic"
            label="車商搜尋"
            value={seller}
            onChange={handleSeller}
          />
        </Box>
        </Grid>
        <Grid item xs={12} sm={4} md={3}>
      <Button variant="contained" type="Submit">
        搜尋
      </Button>
      </Grid>
      </Grid>
    </form>
  );
};

export default FilterForm;
